import UIKit

public class InitViewController: UIViewController, InitViewProtocol {

	var presenter: InitPresenterProtocol?
    
    //MARK: - @IBOutlets
    @IBOutlet weak var containerView: GradientView!
    @IBOutlet weak var photoListView: UIView!
    @IBOutlet weak var albumListView: UIView!
    
    //MARK: - Properties
    var gradientView: GradientView!

    //MARK: - Life cycle
	override public func viewDidLoad() {
        super.viewDidLoad()
        
        setUpView()
    }
    
    override public func viewDidAppear(_ animated: Bool) {
        super.viewDidAppear(animated)
        
        containerView.animateGradient()
    }
    
    //MARK: - Methods
    private func setUpView() {
        photoListView.setBorder(cornerRadius: 10)
        albumListView.setBorder(cornerRadius: 10)
    }
    
    //MARK: - Actions
    @IBAction private func goToPhotoList(_ sender: UIButton) {
        let view = PhotoListRouter.createModule()
        self.navigationController?.pushViewController(view, animated: true)
    }
    
    @IBAction private func goToAlbumList(_ sender: UIButton) {
        let view = AlbumListRouter.createModule()
        self.navigationController?.pushViewController(view, animated: true)
    }
}
